INSERT INTO `wp_calendar_config` VALUES ('can_manage_events','edit_posts'),('calendar_style','    .calnk a:hover {\r\n         background-position:0 0;\r\n         text-decoration:none;  \r\n         color:#000000;\r\n         border-bottom:1px dotted #000000;\r\n         }\r\n    .calnk a:visited {\r\n         text-decoration:none;\r\n         color:#000000;\r\n         border-bottom:1px dotted #000000;\r\n        }\r\n    .calnk a {\r\n        text-decoration:none; \r\n        color:#000000; \r\n        border-bottom:1px dotted #000000;\r\n        }\r\n    .calnk a span { \r\n        display:none; \r\n        }\r\n    .calnk a:hover span {\r\n        color:#333333; \r\n        background:#F6F79B; \r\n        display:block;\r\n        position:absolute; \r\n        margin-top:1px; \r\n        padding:5px; \r\n        width:150px; \r\n        z-index:100;\r\n        }\r\n     .calendar-table {\r\n        border:none;\r\n        width:100%;\r\n     }\r\n     .calendar-heading {\r\n        height:25px;\r\n        text-align:center;\r\n        border:1px solid #D6DED5;\r\n        background-color:#E4EBE3;\r\n     }\r\n     .calendar-next {\r\n        width:25%;\r\n        text-align:center;\r\n     }\r\n     .calendar-prev {\r\n        width:25%;\r\n        text-align:center;\r\n     }\r\n     .calendar-month {\r\n        width:50%;\r\n        text-align:center;\r\n        font-weight:bold;\r\n     }\r\n     .normal-day-heading {\r\n        text-align:center;\r\n        width:25px;\r\n        height:25px;\r\n        font-size:0.8em;\r\n        border:1px solid #DFE6DE;\r\n        background-color:#EBF2EA;\r\n     }\r\n     .weekend-heading {\r\n        text-align:center;\r\n        width:25px;\r\n        height:25px;\r\n        font-size:0.8em;\r\n        border:1px solid #DFE6DE;\r\n        background-color:#EBF2EA;\r\n        color:#FF0000;\r\n     }\r\n     .day-with-date {\r\n        vertical-align:text-top;\r\n        text-align:left;\r\n        width:60px;\r\n        height:60px;\r\n        border:1px solid #DFE6DE;\r\n     }\r\n     .no-events {\r\n\r\n     }\r\n     .day-without-date {\r\n        width:60px;\r\n        height:60px;\r\n        border:1px solid #E9F0E8;\r\n     }\r\n     span.weekend {\r\n        color:#FF0000;\r\n     }\r\n     .current-day {\r\n        vertical-align:text-top;\r\n        text-align:left;\r\n        width:60px;\r\n        height:60px;\r\n        border:1px solid #BFBFBF;\r\n        background-color:#E4EBE3;\r\n     }\r\n     span.event {\r\n        font-size:0.75em;\r\n     }\r\n     .kjo-link {\r\n        font-size:0.75em;\r\n        text-align:center;\r\n     }\r\n     .event-title {\r\n        text-align:center;\r\n        font-weight:bold;\r\n        font-size:1.2em;\r\n     }\r\n     .event-title-break {\r\n        width:96%;\r\n        margin-left:2%;\r\n        margin-right:2%;\r\n        margin-top:5px;\r\n        margin-bottom:5px;\r\n        text-align:center;\r\n        height:1px;\r\n        background-color:#000000;\r\n     }\r\n     .event-content-break {\r\n        width:96%;\r\n        margin-left:2%;\r\n        margin-right:2%;\r\n        margin-top:5px;\r\n        margin-bottom:5px;\r\n        text-align:center;\r\n        height:1px;\r\n        background-color:#000000;\r\n     }\r\n     .calendar-date-switcher {\r\n        height:25px;\r\n        text-align:center;\r\n        border:1px solid #D6DED5;\r\n        background-color:#E4EBE3;\r\n     }\r\n     .calendar-date-switcher form {\r\n        margin:0;\r\n        padding:0;\r\n     }\r\n     .calendar-date-switcher input {\r\n        border:1px #D6DED5 solid;\r\n     }\r\n     .calendar-date-switcher select {\r\n        border:1px #D6DED5 solid;\r\n     }\r\n     .cat-key {\r\n        width:100%;\r\n        margin-top:10px;\r\n        padding:5px;\r\n        border:1px solid #D6DED5;\r\n     }'),('display_author','false'),('display_jump','false'),('display_todays','true'),('display_upcoming','true'),('display_upcoming_days','7'),('calendar_version','1.2'),('enable_categories','false');
